import React, {useState} from "react";
import Resizer from "react-image-file-resizer";
import axios from "axios";
import { useSelector } from "react-redux";
import { Avatar, Button, Badge, Image, Divider, notification } from "antd";
import { UploadOutlined } from '@ant-design/icons';

const FileUpload = ({values, setValues}) => {
  const { user } = useSelector((state) => ({ ...state }));
  const {email, authtoken} = user;
  const [loading, setLoading] = useState(false); 

  const openNotificationWithIcon = (type, msgTitle, msgBody)  => {
    notification[type]({
      message: msgTitle,
      description: msgBody
    });
  };

  const fileUploadAndResize = (e) => {
    let files = e.target.files;
    let allUploadedFiles = values;

    if (files) {
      setLoading(true);
      for (let i = 0; i < files.length; i++) {
        Resizer.imageFileResizer(
          files[i],
          720,
          720,
          "JPEG",
          100,
          0,
          (uri) => {
            axios
              .post(
                `${process.env.REACT_APP_API_URL}/uploadimages`,
                { image: uri , email},
                {
                  headers: {
                    authtoken
                  },
                }
              )
              .then((res) => {
                console.log("IMAGE UPLOAD RES DATA", res);
                setLoading(false);
                allUploadedFiles.push(res.data);
                setValues([ ...values, res.data ]);
              })
              .catch((err) => {
                setLoading(false);
                openNotificationWithIcon('error',err.response.statusText, err.response.data.msg);
              });
          },
          "base64"
        );
      }
    }
  };

  const handleImageRemove = (public_id) => {
    setLoading(true);
    axios
      .post(
        `${process.env.REACT_APP_API_URL}/removeimage`,
        { public_id },
        {
          headers: {
            authtoken: user ? user.token : "",
          },
        }
      )
      .then((res) => {
        setLoading(false);
        const { images } = values;
        let filteredImages = images.filter((item) => {
          return item.public_id !== public_id;
        });
        setValues({ ...values, images: filteredImages });
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  };

  return (
    <>
      <div className="row">
        {values && values.map((image) => (
            <Badge
              count="X"
              key={image.public_id}
              onClick={() => handleImageRemove(image.public_id)}
              style={{ cursor: "pointer" }}
            >
              <Avatar
                src={image.url}
                size={100}
                shape="square"
                className="ml-3"
              /> 
                {/* <Image.PreviewGroup>
                    <Image
                        width={200}
                        height={200}
                        src={image.url}
                        fallback="data:image/png;base64,"
                    />
                </Image.PreviewGroup> */}
            </Badge>
          ))}
      </div>
      <Divider />
      <div>
        <label className="btn btn-primary btn-raised mt-2">
          Choose File
          <input
            type="file"
            multiple
            hidden
            accept="images/*"
            onChange={fileUploadAndResize}
          />
        </label>
      </div>
    </>
  );
};

export default FileUpload;